import React, { useState } from 'react';
import Step1DealBasics from './components/Step1DealBasics';
import Step2CommercialTerms from './components/Step2CommercialTerms';
import './App.css';

function App() {
  const [formData, setFormData] = useState({});
  const [step, setStep] = useState(1);

  const handleProceed = () => {
    setStep(step + 1);
  };

  const handleBack = () => {
    setStep(step - 1);
  };

  return (
    <div className="App" style={{ background: '#111', minHeight: '100vh' }}>
      {step === 1 && (
        <Step1DealBasics
          onProceed={handleProceed}
          formData={formData}
          setFormData={setFormData}
        />
      )}
      {step === 2 && (
        <Step2CommercialTerms
          onProceed={handleProceed}
          onBack={handleBack}
          formData={formData}
          setFormData={setFormData}
        />
      )}
      {step === 3 && (
        <div style={{ color: '#fff', textAlign: 'center', marginTop: 80 }}>
          <h2>Step 3: Payment Terms (Coming Soon)</h2>
        </div>
      )}
    </div>
  );
}

export default App;
